  getToken = () => {
    return localStorage.getItem('token');
  }

  deletePet = (event) => {

    //in button below add a data attribute with the pet's id

    var id = event.target.getAttribute('data-id');

    return _deletePet(id, this.getToken()).then(deletedPetId => {

      let pets = this.state.pets.filter(pet => pet._id !== deletedPetId)

      this.setState({ pets })
    })

  }

  createPet = (event) => {
    event.preventDefault();

    let name = event.target.children[0].value;
    let type = event.target.children[1].value;

    return _createPet(name, type, this.getToken()).then(rj => {
      let pets = [...this.state.pets, rj];
      let placeholder = this.state.purpleRain.split(' ')
      let dummyData = [...this.state.dummyData, this.state.dummyData[0]];
      this.setState({ pets, dummyData })
    })
  }


  componentDidMount() {

    return fetch('https://audd.p.rapidapi.com/findLyrics/?q=drake%20nonstop', {
      method: 'GET',
      headers: {
        "x-rapidapi-host": "audd.p.rapidapi.com",
        "x-rapidapi-key": "b17b8cd89emsh36c1258254b71eap1c8f0djsn38fb874b095d"
      }
    }).then((response) => response.json()
    ).then((responseJson) => {

      var lyrics = responseJson.result[0].lyrics

      var wordSplitter = lyrics.split("\n");
      lyricToGif(wordSplitter)
      console.log(responseJson.result[0].lyrics)

      this.setState({
        isLoading: true,
        items: wordSplitter,
      });
    })
      .catch((error) => {
        console.error(error);
      })

      function lyricToGif(wordSplitter) {

        // var url =
