                      <input type="password" name="password" placeholder="put in a password" />
                      <br></br>
                      <button>Confirm</button>
                    </form>
                    <br></br>
                  </div>}

                <br /><br /><br />
              </div>}

            {this.state.logged_in &&

              <div>
                <h2>Sign Out</h2>

                <form id="logOutForm" onSubmit={this.logout}>
                  <button>Log Out</button>
                </form>

                <br /><br /><br />
